import React, {Component} from 'react';
import '../css/Board.css';
import Note from './Note';

class Board extends Component {
// eslint-disable-next-line
	constructor() {
			super();
			this.state = {
				notes: [
					{
						title: "Class Notes",
						body: "Always use constructors when extending another class"
					},
					{
						title: "My New Address",
						body: "whatever"
					},
					{
						title: "React Notes",
						body: "REverything in react is a component"
					},
					{
						title: "Another Note",
						body: "this note is the fourth one."
					}
				]
			};
	}

	addNote() {
		this.state.notes.push(
			{
				title: "New Note Title",
				body: "New note body"
			}
		);
		this.setState(
			{
				notes: this.state.notes
			}
		);
	}

	render(){
			return (
					<div>
						<div className="div-board">
							<div className="row">
									{
									this.state.notes.map(note => {
										return <Note title={note.title} body={note.body} />
										})
									}
							</div>
						</div>
						<div> 
							<button className="btn btn-success add-button" onClick={this.addNote.bind(this)}>Add</button>
						</div>
					</div>
			);
	}
}

export default Board;
